import React, { useState } from 'react'
import styled from 'styled-components'
import sendIconGrey from '../../assets/send_light-grey.svg'

const MainChat = () => {
    const [message, setMessage] = useState("");

    const handleMessageSubmit = () => {
        console.log(message);
    }

    const handleMessageInput = () => {

    }

    return (
        <StyledArea>
            <ChatArea>
                <InputForm onSubmit={handleMessageSubmit    }>
                        <StyledInput
                            onChange={handleMessageInput}
                            value={message}
                            placeholder="Nachricht..."
                        />
                        {message !== "" && (<StyledButton>
                            <img src={sendIconGrey} alt="send icon" />
                        </StyledButton>)}
                </InputForm>
            </ChatArea>
        </StyledArea>
    );
}

export default MainChat;

const StyledArea = styled.div`
display: grid;
padding: 20px 400px; // originally 20px all sides
background: var(--app-background);
overflow: auto;
height: 100vh;
`;

const ChatArea = styled.div`
position: relative;
background: white;
border-radius: 10px;
display: flex;
`;

const InputForm = styled.form`
display: flex;
width: 100%;
padding: 10px;
margin-top: auto;
`

const StyledInput = styled.input`
border-radius: 6px;
border: 0;
background: var(--light-grey);
padding: 20px;
width: 100%;            
`

const StyledButton = styled.button`
border: 0;
padding: 0px 16px;
margin-left: 5px;
background: var(--petrol);
border-radius: 6px;
`
