import React from 'react'
import styled from 'styled-components'

const SingleMessage = ({username, message, ownMessage}) => {

    return (
        <SingleMessageWrapper ownMessage={ownMessage}>
            <StyledUsername>{username}</StyledUsername>
            <StyledText>{message}</StyledText>
        </SingleMessageWrapper>
    )
};

export default SingleMessage;

const SingleMessageWrapper = styled.div`
display: inline-block;
border-radius: 10px;
padding: 10px;
max-width: 75%;
margin-top: 10px;
background: ${(props) => props.ownMessage ? "var(--petrol-light)" : "var(--light-grey)"};
color: ${(props) => props.ownMessage ? "var(--light-grey)" : "var(--petrol-light)"};
`

const StyledUsername = styled.div`
font-size: 15px;
font-weight: 750;
margin-bottom: 5px
`

const StyledText = styled.div`
font-size: 13px;
`
