import React from 'react'
import styled from 'styled-components'

const SingleMessage = ({currentUsername, name, message, timestamp}) => {

    const formatDate = () => {
        let tmstmp = timestamp.split(',');
        let date = tmstmp[0].replaceAll('/', '.');
        let time = tmstmp[1].split(':');

        console.log(isToday(date));
        if(isToday(date) === "today"){
            date = "today"
        }
        let newTimestamp = date+" "+time[0]+':'+time[1];
        return newTimestamp;
    }

    const isToday = (date) => {
        const today = new Date();
        let dateList = date.split('.');
        if(parseInt(dateList[0]) === today.getDate() 
        && parseInt(dateList[1]) === (today.getMonth()+1)
        && parseInt(dateList[2]) === today.getFullYear()){
            return "today";
        }else if(parseInt(dateList[0]) === today.getDate()+1 
        && parseInt(dateList[1]) === (today.getMonth()+1)
        && parseInt(dateList[2]) === today.getFullYear()){
            return "tomorrow";
        }else{
            return false
        }
    }

    return (
        <OutterWrapper isOwnMessage={currentUsername === name}>
                <SingleMessageWrapper isOwnMessage={currentUsername === name}>
                    <StyledUsername isOwnMessage={currentUsername === name}>{name}</StyledUsername>
                    <StyledText>{message}</StyledText>
                    <StyledTimestamp isOwnMessage={currentUsername === name}>{formatDate()}</StyledTimestamp>
                </SingleMessageWrapper>
        </OutterWrapper>
    )
};

export default SingleMessage;

const OutterWrapper = styled.div`
display: block;
text-align: ${(props) => props.isOwnMessage ? "right" : "left"};
`

const SingleMessageWrapper = styled.div`
display: inline-block;
border-radius: 10px;
padding: 10px;
max-width: 75%;
margin-bottom: 10px;
background: ${(props) => props.isOwnMessage ? "var(--petrol-light)" : "var(--light-grey)"};
color: ${(props) => props.isOwnMessage ? "var(--light-grey)" : "var(--petrol-light)"};
`

const StyledUsername = styled.div`
font-size: 15px;
font-weight: 750;
margin-bottom: 5px;
display: ${(props) => props.isOwnMessage ? "none" : ""};
`

const StyledText = styled.div`
font-size: var(--p-size);
overflow-wrap: break-word;
white-space: pre-wrap;
text-align: left;
`

const StyledTimestamp = styled.div`
font-size: var(--p-small-size);
margin-top: 5px;
text-align: ${(props) => props.isOwnMessage ? "right" : "left"} ;
color: ${(props) => props.isOwnMessage ? "var(--light-grey)" : "var(--light-petrol)"} ;
`
