import React, { useState, useEffect, useRef } from 'react'
import styled from 'styled-components'
import sendIconGrey from '../../assets/send_light-grey.svg'
import saveIconGrey from '../../assets/save_light-grey.svg'
import SingleMessage from './SingleMessage'
import io from 'socket.io-client'

const MainChat = () => {
    const [state, setState] = useState({name: "", message: "" });
    const [headerName, setHeaderName] = useState("");
    const [chat, setChat] = useState([]);
    const [isUserNamePopupVisible, setIsUsernamePopupVisible] = useState(true);

    const socketRef = useRef();

    useEffect(()=> {
        socketRef.current = io.connect("http://localhost:5000")
        socketRef.current.on('message', ({ name, message }) => {
            console.log(chat);
            setChat([ ...chat, { name, message } ])
        })
        return () => socketRef.current.disconnect()
    },
    [ chat ])


    const onUsernameSubmit = (event) => {
        event.preventDefault();
        setState({...state, [event.target.name]: event.target.value});
        setIsUsernamePopupVisible(false);
        setHeaderName(state.name);
    }

    const onMessageSubmit = (event) => {
        const { name, message } = state;
        socketRef.current.emit('message', { name, message});
        event.preventDefault();
        setState({message: "", name });
    }

    const onTextInputChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.value });
    }

    return (
        <StyledArea>
            {isUserNamePopupVisible && <Overlay>
                <Popup>
                    <PopupDescription>
                        Insert your Username!
                    </PopupDescription>
                    <InputForm onSubmit={onUsernameSubmit}>
                        <StyledInput
                            name="name"
                            onChange={onTextInputChange}
                            value={state.name}
                            placeholder="Username..."
                            autoComplete="off"
                        />
                        <StyledButton text={state.name}>
                            <img src={saveIconGrey} alt="save icon from feathericons.com" />
                        </StyledButton>
                    </InputForm>
                </Popup>
            </Overlay>}
            <ChatArea>
                <HeaderWrapper>
                    <Header>
                        {headerName}
                    </Header>
                </HeaderWrapper>
                <MessagesWrapper>
                    {chat.map(({name, message}, index) => {
                        return(
                            <SingleMessage 
                                key={index}
                                currentUsername={state.name}
                                name={name}
                                message={message}
                                />
                                );
                            })}
                </MessagesWrapper>
                <InputForm onSubmit={onMessageSubmit}>
                        <StyledInput
                            name="message"
                            onChange={onTextInputChange}
                            value={state.message}
                            placeholder="Nachricht..."
                            autoComplete="off"
                        />
                        <StyledButton text={state.message}>
                            <img src={sendIconGrey} alt="send icon from feathericons.com" />
                        </StyledButton>
                </InputForm>
            </ChatArea>
        </StyledArea>
    );
}

export default MainChat;

const StyledArea = styled.div`
padding: 20px;
background: var(--app-background);
overflow: auto;
height: 100vh;
`;

const Overlay = styled.div`
background: var(--trans-black);
position: fixed;
width: 100%;
height: 100%;
top: 0;
left: 0;
right: 0;
bottom: 0;
z-index: 2;
`

const Popup = styled.div`
position: absolute;
top: 50%;
left: 50%;
transform: translate(-50%, -50%);
background: var(--white);
border-radius: 10px;
`

const PopupDescription = styled.div`
text-align:center;
padding-top: 10px;
`
const ChatArea = styled.div`
// position: absolute;
// top:50%;
// left:50%;
display: flex;
flex-direction: column;
background: var(--white);
border-radius: 10px;
// height: 100%;
// max-width: 800px;
`;

const HeaderWrapper = styled.div`
padding: 10px;
border-bottom: 1px solid var(--light-grey);
`

const Header = styled.div`
display: flex;
width: 100%;
padding: 10px;
margin-top: auto;
background: var(--light-grey);
border-radius: 10px;
padding: 15px;
color: var(--petrol);
font-weight: 700;
`

const MessagesWrapper = styled.div`
display:flex;
flex-direction: column;
justify-content:flex-end;
overflow:hidden;
padding: 10px;
height: 100%;
overflow-y: auto;
`

const InputForm = styled.form`
display: flex;
width: 100%;
padding: 10px;
margin-top: auto;
`

const StyledInput = styled.input`
border-radius: 10px;
border: 0;
background: var(--light-grey);
padding: 20px;
width: 100%;

&:focus {
    outline: none;
}
`

const StyledButton = styled.button`
border: 0;
padding: 4px 16px 0px 15px;
margin-left: 5px;
background: ${(props) => props.text !== "" ? "var(--petrol-light)" : "var(--dark-grey)"};
border-radius: 10px;
cursor: pointer;
cursor: ${(props) => props.text !== "" ? "pointer" : "default"};
pointer-events: ${(props) => props.text !== "" ? "" : "none"};   

&:hover {
    background: ${(props) => props.text !== "" ? "var(--petrol)" : ""};
}
`
