import React from 'react'
import styled from 'styled-components'

const SingleMessage = ({username, message, isOwnMessage}) => {

    return (
        <OutterWrapper>
            <InnerWrapper>
                <SingleMessageWrapper isOwnMessage={isOwnMessage}>
                    <StyledUsername isOwnMessage={isOwnMessage}>{username}</StyledUsername>
                    <StyledText isOwnMessage={isOwnMessage}>{message}</StyledText>
                </SingleMessageWrapper>
            </InnerWrapper>
        </OutterWrapper>
    )
};

export default SingleMessage;

const OutterWrapper = styled.div`
display: block;
`

const InnerWrapper = styled.div`
    align-items: center;
    justify-content: center;
`

const SingleMessageWrapper = styled.div`
display: inline-block;
border-radius: 10px;
padding: 10px;
max-width: 75%;
margin-top: 10px;
background: ${(props) => props.isOwnMessage ? "var(--petrol-light)" : "var(--light-grey)"};
color: ${(props) => props.isOwnMessage ? "var(--light-grey)" : "var(--petrol-light)"};
`

const StyledUsername = styled.div`
text-align: ${(props) => props.isOwnMessage ? "left" : "right"};
font-size: 15px;
font-weight: 750;
margin-bottom: 5px
`

const StyledText = styled.div`
text-align: ${(props) => props.isOwnMessage ? "left" : "right"};
font-size: 13px;
`
