import React, { useState } from 'react'
import styled from 'styled-components'
import sendIconGrey from '../../assets/send_light-grey.svg'
import SingleMessage from './SingleMessage'

const MainChat = () => {
    const [message, setMessage] = useState("");
    // testing
    const messages = [
        {
            id: 1,
            username: "username 1",
            message: "Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.",
            isOwnMessage: true,
        },
        {
            id: 2,
            username: "username 2",
            message: "Lorem ipsum dolor sit amet",
            isOwnMessage: false,
        },
        {
            id: 3,
            username: "username 3",
            message: "Lorem ipsum dolor sit amet, consectetuer adipiscing elit.",
            isOwnMessage: true,
        },
    ]

    const handleMessageSWrapperubmit = (event) => {
        event.preventDefault();
        console.log("submit: ",message);
        setMessage("");
    }

    const handleMessageInput = (event) => {
        setMessage(event.target.value);
    }

    return (
        <StyledArea>
            <ChatArea>
                <MessagesWrapper>
                    {messages.map((msg) => {
                        return(
                            <SingleMessage 
                                key={msg.id}
                                username={msg.username}
                                message={msg.message}
                                isOwnMessage={msg.isOwnMessage}
                            />
                        );
                    })}
                </MessagesWrapper>
                <InputForm onSubmit={handleMessageSWrapperubmit    }>
                        <StyledInput
                            onChange={handleMessageInput}
                            value={message}
                            placeholder="Nachricht..."
                        />
                        <StyledButton message={message}>
                            <img src={sendIconGrey} alt="send icon" />
                        </StyledButton>
                </InputForm>
            </ChatArea>
        </StyledArea>
    );
}

export default MainChat;

const StyledArea = styled.div`
padding: 20px 400px;
background: var(--app-background);
overflow: auto;
height: 100vh;
`;

const ChatArea = styled.div`
display: flex;
flex-direction: column;
background: var(--white);
border-radius: 10px;
height: 100%;
`;

const MessagesWrapper = styled.div`
padding: 10px;
height: 100%;
overflow-y: auto;
`

const InputForm = styled.form`
display: flex;
width: 100%;
padding: 10px;
margin-top: auto;
`

const StyledInput = styled.input`
border-radius: 10px;
border: 0;
background: var(--light-grey);
padding: 20px;
width: 100%;

&:focus {
    outline: none;
}
`

const StyledButton = styled.button`
border: 0;
padding: 4px 16px 0px 15px;
margin-left: 5px;
background: ${(props) => props.message !== "" ? "var(--petrol-light)" : "var(--dark-grey)"};
border-radius: 10px;
cursor: pointer;
cursor: ${(props) => props.message !== "" ? "pointer" : "default"};
pointer-events: ${(props) => props.message !== "" ? "" : "none"};   

&:hover {
    background: ${(props) => props.message !== "" ? "var(--petrol)" : ""};
}
`
