import React, { useState } from 'react'
import styled from 'styled-components'
import sendIconGrey from '../../assets/send_light-grey.svg'
import SingleMessage from './SingleMessage'

const MainChat = () => {
    const [message, setMessage] = useState("");

    const handleMessageSubmit = (event) => {
        event.preventDefault();
        console.log("submit: ",message);
        setMessage("");
    }

    const handleMessageInput = (event) => {
        setMessage(event.target.value);
    }

    return (
        <StyledArea>
            <ChatArea>
                <Messages>
                    <SingleMessage username="Username1"
                        message="Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus."
                        ownMessage={true}/>
                    <SingleMessage username="Username2"
                        message="Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus."
                        ownMessage={false}/>
                </Messages>
                <InputForm onSubmit={handleMessageSubmit    }>
                        <StyledInput
                            onChange={handleMessageInput}
                            value={message}
                            placeholder="Nachricht..."
                        />
                        <StyledButton message={message}>
                            <img src={sendIconGrey} alt="send icon" />
                        </StyledButton>
                </InputForm>
            </ChatArea>
        </StyledArea>
    );
}

export default MainChat;

const StyledArea = styled.div`
display: grid;
padding: 20px 400px; // originally 20px all sides
background: var(--app-background);
overflow: auto;
height: 100vh;
`;

const ChatArea = styled.div`
position: relative;
background: var(--white);
border-radius: 10px;
display: flex;
flex-direction: column;
`;

const Messages = styled.div`
padding: 20px;
height: 100%;
overflow-y: auto;
`

const InputForm = styled.form`
display: flex;
width: 100%;
padding: 10px;
margin-top: auto;
`

const StyledInput = styled.input`
border-radius: 10px;
border: 0;
background: var(--light-grey);
padding: 20px;
width: 100%;

&:focus {
    outline: none;
}
`

const StyledButton = styled.button`
border: 0;
padding: 4px 16px 0px 15px;
margin-left: 5px;
background: ${(props) => props.message !== "" ? "var(--petrol-light)" : "var(--dark-grey)"};
border-radius: 10px;
cursor: pointer;
cursor: ${(props) => props.message !== "" ? "pointer" : "default"};
pointer-events: ${(props) => props.message !== "" ? "" : "none"};   

&:hover {
    background: ${(props) => props.message !== "" ? "var(--petrol)" : ""};
}
`
