import React from 'react'
import styled from 'styled-components'

const MainChat = () => {
    return (
        <StyledArea>
            <ChatArea>
                <InputForm onSubmit={()=>console.log("message submit!")}>
                    <NewMessageWrapper>
                        {/* <StyledInput/>
                        <StyledButton/> */}
                    </NewMessageWrapper>
                </InputForm>
            </ChatArea>
        </StyledArea>
    );
}

export default MainChat;

const StyledArea = styled.div`
display: grid;
padding: 20px 400px; // originally 20px all sides
background: var(--app-background);
overflow: auto;
height: 100vh;
`;

const ChatArea = styled.div`
position: relative;
background: white;
border-radius: 10px;
display: flex;
`;

const InputForm = styled.form`
display: flex;
padding: 20px;
margin-top:auto;
`

const NewMessageWrapper = styled.div`
padding: 20px;
width: 100%;
overflow-y: auto;
background-color: var(--app-background) ;
`

const StyledInput = styled.input`
border: 0;
border-radius: 6px;
// display: block;
width: 100%;
padding: 20px;
// font-size: 18px;
color: var(--black);
background: var(--light-grey);
`

const StyledButton = styled.button`
display: inline-block;
text-decoration: none;

`
